//题意：给定两个二叉树s,t，判断t是不是s的子结构（完全匹配），而不是包含

//思路：递归

/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode(int x) : val(x), left(NULL), right(NULL) {}
 * };
 */

//TLE了 ，为什么？
//这种方式对每个s每个节点都递归了一次
class Solution {
public:
    bool isSubtree(TreeNode* s, TreeNode* t) {
        return issubstree(s,t,t);
    }

    bool issubstree(TreeNode *s,TreeNode *t,TreeNode* origint)
    {
        //但凡有不合适的就从新找t(这个t是最原始的t,origint表示)
        if(s == NULL && t == NULL) return true;
        if(!(s != NULL && t!=NULL)) return false;
        bool flag = false;
        if(s->val == t->val)
                flag |= (issubstree(s->left,t->left,origint) && issubstree(s->right,t->right,origint));
        if(flag) return flag;
            //试着看子数是不是匹配
        flag |= issubstree(s->left,origint,origint);        
        if(flag) return flag;
        flag |= issubstree(s->right,origint,origint);
        return flag;
    }
};


