//我的AC
class Solution {
public:
    bool isSubtree(TreeNode* s, TreeNode* t) {
        if( s == NULL || t == NULL) return false;
        return is_substree(s,t)
            || isSubtree(s->left,t)
            || isSubtree(s->right,t);
    }
    bool is_substree(TreeNode*s,TreeNode*t)
    {
        if(t == NULL && s == NULL) return true;
        if(!(t != NULL && s!=NULL)) return false;
        if(s->val == t->val)
            return is_substree(s->left,t->left)
                && is_substree(s->right,t->right);
        return false;
    }
};


//大神的AC代码
//分层递归。并非所有问题都用一个递归函数
//对于每次isSubtree，写一个专门判断是否完全相同的函数isSame。如果不同则返回false.
//然后以s,t为root的树不匹配，则对于（s->left,t）和 (s->right,t)来判断，然后每次判断还用isSame
class Solution {
public:
    bool same(TreeNode* s, TreeNode* t) {
        if (s == nullptr) {
            return t == nullptr;
        }
        if (t == nullptr) {
            return s == nullptr;
        }
        return s->val == t->val && same(s->left, t->left) && same(s->right, t->right);
    }
    bool isSubtree(TreeNode* s, TreeNode* t) {
        return same(s, t) || 
            (s != nullptr && isSubtree(s->left, t)) ||
            (s != nullptr && isSubtree(s->right, t));
    }
};